import './App.css';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './Components/Home';
import Login from './Components/login'; // Ensure Login component is correctly imported
import { auth, provider } from './Components/firebaseConfig';
import { useState } from 'react';
import { signInWithPopup } from 'firebase/auth';

function App() {
  const [user, setUser] = useState(null);

  const handleGoogleSignIn = () => {
    signInWithPopup(auth, provider).then((result) => {
      const user = result.user;
      console.log(user);
      setUser(user);
    }).catch((err) => {
      console.log(err);
    })
  }
  const handleLogOut = () => {
    setUser(null);
  }

  return (
    <Router>
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/login' element={<Login />} />

        {/* If user is logged in, show their info; otherwise, redirect to login */}
        <Route path="/profile" element={
          user ? (
            <div className='wrapper'>
              <div className='box'>
                <button className=' btn btn-primary btn-md' onClick={handleLogOut}>
                  Log Out
                </button>
                <h3>{user.displayName}</h3>
                <p>{user.email}</p>
                <div className='photo'>
                  <img src={user.photoURL} alt='dp'></img>
                </div>
              </div>
            </div>
          ) : (
            <Navigate to='/login' />
          )
        } />
      </Routes>
    </Router>
  );
}

export default App;
